});
cy.get('.text-center a[href="/view_cart"]').as("viewCart").should("be.visible");
cy.get("@viewCart").invoke("text").then(viewCart => {
  cy.get("@viewCart").parent(".text-center").click();
  expect(viewCart).to.eq("View Cart");
});

cy.get("a[href='/view_cart']").parent("li").click();
cy.get("table tbody tr").each(($row, index, $rows) => {
  cy.wrap($row).within(() => {
    cy.get("td").eq(2).invoke("text").then(priceTxt => {
      const price = parseFloat(priceTxt.replace("Rs.", "").trim());
      cy.get("td").eq(3).invoke("text").then(unitTxt => {
        const unit = parseFloat(unitTxt);
        const calculatedTotal = price * unit;
        cy.get("td").eq(4).invoke("text").then(GrandTotal => {
          const displayedTotal = parseFloat(GrandTotal.replace("Rs.", ""));
          expect(calculatedTotal).to.eq(displayedTotal);
          cy.log(price + "" + unit);
        });
      });
    });
  });
});

